import React from "react";
import {compose} from "redux";
import {addContact, getContacts} from "../../Redux/contacts-reducer";
import {witchAuthRedirect} from "../../Redirect/authRedirect";
import PersonalArea from "./PersonalArea";
import {connect} from "react-redux";

class PersonalAreaContainer extends React.Component {
    componentDidMount() {
        this.props.getContacts()
    }
    addContactSubmit(values) {
        const {name, status} = values
        props.addContact(name, status)
    }
    render() {
        return (
            <PersonalArea addContactSubmit={this.addContactSubmit} contacts = {this.props.contacts}/>
        )
    }
}

const mapStateToProps = (state) => ({
    contacts: state.contactsPage.contacts,
    isAuth: state.auth.isAuth,
})

export default compose(
    connect(mapStateToProps, {getContacts, addContact}),
    witchAuthRedirect
) (PersonalAreaContainer)
