import {coreAPI} from "./api"

export const authAPI = {
    login(login, password, resultCode, isAuth, id) {
      return coreAPI.post(`profile`, {login, password, resultCode, isAuth, id})
          .then(res => res.data[0])
    },
    logout() {
        let resultCode = 0
        let login = ""
        let isAuth = false
        coreAPI.delete(`profile/1`)
            .then(res => res.data)
        coreAPI.post(`profile/1`, {resultCode})
    },
    checkAuth() {
        return coreAPI.get(`profile`).then(res => res.data[0])
    }
}
