import {coreAPI} from "./api"

export const authAPI = {
    login(login, password) {
      return coreAPI.post(`profile`, {login, password})
          .then(res => res.data)
    },
    logout() {
        return coreAPI.delete(`profile`)
    },
    checkAuth() {
        return coreAPI.get(`profile`).then(res => res)
    }
}
