import React from "react";
import f from "./ContactsForm.module.css"
import {Field, Form, Formik} from "formik";

export const AddContactsForm = ({addContactSubmit}) => {
    const submit = (values, {setSubmitting}) => {
        addContactSubmit(values)
        setSubmitting(false)
    }
    return (
        <div className={f.addContactsForm}>
            <Formik
                initialValues={{name: '',
                                email: '',
                                phoneNumber: '',
                                position: ''
                }}
                onSubmit={submit}
            >
                {({ isSubmitting, errors, touched }) => (
                    <Form>
                        <Field type="text" name="name" placeholder = "Name" />
                        <Field type="email" name="email" placeholder = "Email" validate={validateEmail} />
                        {errors.email && touched.email ? <div> {errors.email} </div> : null}
                        <Field type="tel" name="phoneNumber" placeholder = "Phone Number" />
                        <Field as="select" name="position">
                            <option value="Owner">Owner</option>
                            <option value="CTO">CTO</option>
                            <option value="Project Manager">Project Manager</option>
                            <option value="Team Lead">Team Lead</option>
                            <option value="Senior developer">Senior developer</option>
                            <option value="Middle Developer">Senior developer</option>
                            <option value="Junior Developer">Senior developer</option>
                        </Field>
                        <button type="submit" disabled={isSubmitting}>
                            Add contact
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    )
}

const validateEmail = (value) => {
    let errorMessage
    if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(value)) {
        errorMessage = 'Invalid email address';
    }
    return errorMessage
}
