import {contactsAPI} from "../API/contacts-api";

const initialState = {
    contacts: [],
}

const contactsReducer = (state = initialState, action) => {
    switch (action.type) {
        case "FrontEnd/practice/SET_CONTACT":
            return {...state, contacts: action.contacts}
        case "FrontEnd/practice/ADD_CONTACT":
            return {...state.contacts.push(action.contacts)}
        default:
            return state
    }
}

export const actions = {
    setContact: (contacts) => ({type: "FrontEnd/practice/SET_CONTACT", contacts}),
    addContact: (contact) => ({type: "FrontEnd/practice/ADD_CONTACT", contact})
}
export const getContacts = () => {
    return async (dispatch) => {
        const data = await contactsAPI.getContacts()
        dispatch(actions.setContact(data))
    }
}
export const addContact = (name, status) => {
    return async (dispatch) => {
        await contactsAPI.addContacts(name, status)
        dispatch(getContacts())
    }
}


export default contactsReducer
