import {coreAPI} from "./api";

export const contactsAPI = {
    getContacts() {
        return coreAPI.get(`contacts`)
            .then(res => res.data)
    },
    addContacts(status, name) {
        return coreAPI.post(`contacts`)
            .then(res => {res.data})
    },
    deleteContacts(userID) {
        return coreAPI.delete(`contacts/${userID}`)
    },
    updateStatus(status) {
        return coreAPI.put(`contacts/status/`, {status: status})
    }
}
