import React, {useState} from "react";
import c from "./contacts.module.css"
import f from "../ContactForms/ContactsForm.module.css";
import {Field, Form, Formik} from "formik";
import Contact from "./Contact/Contact";

const ContactsList = ({deleteContact, contacts, updateContact}) => {
    const [editMode, setEditMode] = useState(false)

    return (

        <div className={c.contactsContainer}>
            {!editMode
                ? <Contact deleteContact={deleteContact}
                           goToEditMode={() => setEditMode(true)}
                           contacts={contacts}
                />
                : <ContactEditForm updateContact={updateContact}
                                   endEditMode={() => setEditMode(false)}
                                   id = {contacts.id}
                />
            }
        </div>
    )
}


const ContactEditForm = ({updateContact, endEditMode, id}) => {
    const submit = (values, {setSubmitting}) => {
        values.id = id
        updateContact(values)
        setSubmitting(false)
        endEditMode()
    }
    return (
        <div className={f.addContactsForm}>
            <Formik
                initialValues={{ name: '', status: ''}}
                validate={addContactFormValidate}
                onSubmit={submit}
            >
                {({ isSubmitting }) => (
                    <Form>
                        <Field type="text" name="name" />
                        <Field type="text" name="status" />
                        <button type="submit" disabled={isSubmitting}>
                            Save
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    )
}
const addContactFormValidate = (values) => {
    const errors = {}
    return errors
}
export const ContactsSearchForm = ({onFilterChanged}) => {
    const submit = (values, {setSubmitting}) => {
        onFilterChanged(values)
        setSubmitting(false)
    }

    return (
        <div>
            <Formik
                initialValues={{ term: ''}}
                validate={addContactFormValidate}
                onSubmit={submit}
            >
                {({ isSubmitting }) => (
                    <Form>
                        <Field type="text" name="term" />
                        <button type="submit" disabled={isSubmitting}>
                            Find
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    )
}

export default ContactsList
