import React from "react";
import f from "./ContactsForm.module.css"
import {Field, Form, Formik, useFormik} from "formik";




export const ContactsSearchForm = ({onFilterChanged}) => {
    const submit = (values, {setSubmitting}) => {
        onFilterChanged(values)
        setSubmitting(false)
    }

    return (
        <div className={f.contactsSearchForm}>
            <Formik
                initialValues={{ term: ''}}
                onSubmit={submit}
            >
                {({ isSubmitting }) => (
                    <Form>
                        <Field type="text" name="term" />
                        <button type="submit" disabled={isSubmitting}>
                            Find
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    )
}

const FormContact = ({ submit, name, endEditMode}) => {

    return (
        <Formik
            initialValues={{
                name: '',
                email: '',
                phoneNumber: '',
                position: '',
            }}
            onSubmit={submit}
        >
            {({ isSubmitting, errors, touched }) => (
                <Form>
                    <Field className={f.name} type="text" name="name" placeholder = "Name" validate={validateName} />
                    {errors.name && touched.name ? <div className={f.error}> {errors.name} </div> : null}
                    <Field className={f.email} type="email" name="email" placeholder = "Email" validate={validateEmail} />
                    {errors.email && touched.email ? <div className={f.error}> {errors.email} </div> : null}
                    <Field className={f.phone} type="tel" name="phoneNumber" placeholder = "Phone Number" validate={validateNumber}/>
                    {errors.phoneNumber && touched.phoneNumber ? <div className={f.error}> {errors.phoneNumber} </div> : null}
                    <Field className={f.position} as="select" name="position">
                        <option value="Owner">Owner</option>
                        <option value="CTO">CTO</option>
                        <option value="Project Manager">Project Manager</option>
                        <option value="Team Lead">Team Lead</option>
                        <option value="Senior developer">Senior developer</option>
                        <option value="Middle Developer">Middle Developer</option>
                        <option value="Junior Developer">Junior Developer</option>
                    </Field>
                    <button type="submit" disabled={isSubmitting}>
                        {name === "Added"
                            ? "Add contact"
                            : "Edit contact"
                        }
                    </button>
                    {name === 'Edit'
                        ? <button onClick={endEditMode}>Back</button>
                        : null
                    }
                </Form>
            )}
        </Formik>
    )
}

