import React from "react";
import f from "./addContactsForm.module.css"
import {Formik} from "formik";

const AddContactsForm = (props) => {
    const submit = (values, {setSubmitting}) => {
        props.addContactSubmit(values)
        setSubmitting(false)
    }
    return (
        <div className={f.addContactsForm}>
            <Formik
                initialValues={{ term: '', friend: null}}
                validate={usersSearchFormValidate}
                onSubmit={submit}
            >
                {({ isSubmitting }) => (
                    <Form>
                        <Field type="text" name="term" />
                        <Field name="friend" as="select">
                            <option value="null">All</option>
                            <option value="true">Only followed</option>
                            <option value="false">Blue unFollowed</option>
                        </Field>
                        <button type="submit" disabled={isSubmitting}>
                            Find
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    )
}

const addContactFormValidate = (values) => {
    const errors = {}
    return errors
}

export default AddContactsForm
