import {coreAPI} from "./api";

export const contactsAPI = {
    getContacts() {
        return coreAPI.get(`contacts`)
            .then(res => res.data)
    },
    addContacts(status, name) {
        return coreAPI.post(`contacts`, {name, status})
            .then(res => res.data)
    },
    deleteContact(userID) {
        return coreAPI.delete(`contacts/${userID}`)
    },
    updateStatus(id,status) {
        return coreAPI.post(`contacts/${id}`, {status: status})
    }
}

contactsAPI.updateStatus(2,"yeasd")
