import {coreAPI} from "./api"

export const authAPI = {
    login(login, password, resultCode, isAuth, id) {
      return coreAPI.post(`profile`, {login, password, resultCode, isAuth, id})
          .then(res => res.data)
    },
    logout() {
        return coreAPI.delete(`profile/1`)
    },
    checkAuth() {
        coreAPI.get(`profile`).then(res => res.data)
    }
}
