import React, {useState} from "react";
import c from "./contacts.module.css"
import f from "../AddContactsForm/addContactsForm.module.css";
import {Field, Form, Formik} from "formik";

const Contacts = ({name, status, deleteContact, contacts, updateContact, onFilterChanged}) => {
    const [editMode, setEditMode] = useState(false)

    return (

        <div className={c.contactsContainer}>
            <ContactsSearchForm onFilterChanged={onFilterChanged} />
            {!editMode
                ?   <ContactData status={status}
                                 name={name}
                                 deleteContact={deleteContact}
                                 goToEditMode={() => setEditMode(true)}
                                 contacts={contacts}
                />
                : <ContactEditForm updateContact={updateContact}
                                   endEditMode={() => setEditMode(false)}
                                   id = {contacts.id}
                />
            }
        </div>
    )
}

const ContactData = ({name, status, deleteContact, contacts, goToEditMode}) => {
    return (
        <div>
            <div>{name}</div>
            <div>{status}</div>
            <button onClick={() => {deleteContact(contacts.id)}}>Delete</button>
            <button onClick={goToEditMode}>Edit</button>
        </div>
    )
}
const ContactEditForm = ({updateContact, endEditMode, id}) => {
    const submit = (values, {setSubmitting}) => {
        values.id = id
        updateContact(values)
        setSubmitting(false)
        endEditMode()
    }
    return (
        <div className={f.addContactsForm}>
            <Formik
                initialValues={{ name: '', status: ''}}
                validate={addContactFormValidate}
                onSubmit={submit}
            >
                {({ isSubmitting }) => (
                    <Form>
                        <Field type="text" name="name" />
                        <Field type="text" name="status" />
                        <button type="submit" disabled={isSubmitting}>
                            Save
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    )
}
const addContactFormValidate = (values) => {
    const errors = {}
    return errors
}
export const ContactsSearchForm = ({onFilterChanged}) => {
    const submit = (values, {setSubmitting}) => {
        onFilterChanged(values)
        setSubmitting(false)
    }

    return (
        <div>
            <Formik
                initialValues={{ term: '', friend: null}}
                validate={addContactFormValidate}
                onSubmit={submit}
            >
                {({ isSubmitting }) => (
                    <Form>
                        <Field type="text" name="term" />
                        <Field name="friend" as="select">
                            <option value="null">All</option>
                            <option value="true">Only followed</option>
                            <option value="false">Blue unFollowed</option>
                        </Field>
                        <button type="submit" disabled={isSubmitting}>
                            Find
                        </button>
                    </Form>
                )}
            </Formik>
        </div>
    )
}

export default Contacts
