import {coreAPI} from "./api"

export const authAPI = {
    login(login, password, resultCode, isAuth, id) {
      return coreAPI.post(`comments`, {login, password, resultCode, isAuth, id})
          .then(res => res.data)
    },
    logout() {
        return coreAPI.delete(`comments/1`)
    },
    checkAuth() {
        return coreAPI.get(`comments`).then(res => res.data)
    }
}
